def keyboard_city():
	keyboard = InlineKeyboardMarkup()
	for something in os.listdir('./sity'):
		if os.path.isdir(f'./sity/{something}'):
			keyboard.add(InlineKeyboardButton(citys[something], callback_data=something))
	return keyboard

def keyboard_phone(phone):
	keyboard = InlineKeyboardMarkup()
	keyboard.add(InlineKeyboardButton('–û—Ç–∫—Ä—ã—Ç—å –∏–º—è(10 –†.)', callback_data=f'phone{phone}'))
	return keyboard